var obj = JSON.parse(msg.payload);
node.warn('	msg.payload:'+	msg.payload);
delete msg.payload;
delete msg._session;

// toggle switch tsw-1	- output 1 
if(obj.id=="init"){
    context.global.selectedType=obj.v;
    msg.payload = {id:"init_btn", v: context.global.selectedType}; 
	return [msg,null];
}else if(obj.id=="change_type"){
	context.global.selectedType=obj.v;
	msg.url = 'http://localhost:3001/todos/lists?name=finalist&type='+obj.v;
	node.warn('	msg.url:'+	msg.url);
	return [null,msg];
}

//node.warn('Get Device Information');
var msgTools = context.global.msgTools;
var finalist = msg.payload;

var dataString = msgTools.getFinalData(finalist);

msg.payload = {id:"change_table", v: dataString,type: context.global.selectedType}; 
node.warn('Get Device Information msg.payload:\n'+JSON.stringify(msg.payload));

return msg;

//node.warn('Check WS/devices Message payload'+msg.payload);
var obj = JSON.parse(msg.payload);
delete msg.payload;
delete msg._session;

// toggle switch tsw-1	- output 1 
if(obj.id=="init" || obj.id=="refresh"){
    var json = obj.v;
    var mac = obj.v.mac;
    var type = obj.v.type;
    var date = obj.v.date;
    var option = obj.v.option;
    var host = obj.v.host;
    var port = obj.v.port;
    context.global.selectedType = type;
    msg.url = "http://"+host+":"+port+"/todos/devices?mac="+mac+"&type="+type+"&mdate="+date+"&option="+option;
    node.warn(msg.url);
	return msg;  
}

//node.warn('Get Device Information');
var msgTools = context.global.msgTools;
var type = context.global.selectedType;
var devices = msg.payload;

var dataString = msgTools.getDevicesData(type,devices);

msg.payload = {id:"init_table", v: dataString,type: context.global.selectedType}; 
